function inputIsValidEmailAddress(email){
  var regex = /\S+@\S+\.\S+/;
  return regex.test(email);
}

function getDomainNameFromEmailAddress(email){
  var domainName = email.substring(email.lastIndexOf("@") +1);
  return domainName
}

module.exports = {
  inputIsValidEmailAddress: inputIsValidEmailAddress,
  getDomainNameFromEmailAddress: getDomainNameFromEmailAddress
};
